dict.string:
#!/bin/bash

query="{query}"

if [ ${#query} -lt 3 ]; then

cat << EOF
{"items": [
    {
        "uid": "result",
		"valid": false,
        "title": "Lookup Area Code $query",
		"subtitle": "Area codes must be three digits long"
    }
]}
EOF

exit

elif [ ${#query} -gt 3 ]; then

cat << EOF
{"items": [
    {
        "uid": "result",
		"valid": false,
        "title": "Invalid Area Code $query",
		"subtitle": "Area codes must be three digits long"
    }
]}
EOF

exit

fi

grep '{query}' area_codes.csv > /dev/null
exists=$?

if [ $exists -ne 0 ]; then

cat << EOF
{"items": [
    {
        "uid": "result",
        "title": "Area Code Not Found: $query",
		"subtitle": "All area codes are 3 digit numbers â‰¥200",
		"valid": false
    }
]}
EOF


exit

else

desc=$(sed -nr 's/^{query},"?([A-Za-z0-9\, ]+)"?/\1/p' area_codes.csv | tr -d '\n\r')

cat << EOF
{"items": [
    {
        "uid": "result",
        "title": "Area Code $query: $desc"
    }
]}
EOF

exit

fi
